package com.example.root.dicodingmovie;import android.content.Context;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.Button;import android.widget.ImageView;import android.widget.TextView;import com.squareup.picasso.Picasso;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.Date;public class DetailMovieActivity extends AppCompatActivity {    public static String EXTRA_TITLE        = "extra_title";    public static String EXTRA_OVERVIEW     = "extra_overview";    public static String EXTRA_RELEASE_DATE = "extra_release_date";    public static String EXTRA_POSTER_JPG   = "extra_poster_jpg";    private TextView Judul, Overview, ReleaseDate;    private ImageView Poster;    private Context context;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_detail_movie);        Judul       = (TextView)findViewById(R.id.detailTitle);        Overview    = (TextView)findViewById(R.id.detailOverview);        ReleaseDate = (TextView)findViewById(R.id.detailRelease);        Poster      = (ImageView)findViewById(R.id.detailBackdrop);        String getTitle     = getIntent().getStringExtra(EXTRA_TITLE);        String getOverview  = getIntent().getStringExtra(EXTRA_OVERVIEW);        String getRelease   = getIntent().getStringExtra(EXTRA_RELEASE_DATE);        String getPosterPath= getIntent().getStringExtra(EXTRA_POSTER_JPG);        Judul.setText(getTitle);        Overview.setText(getOverview);        SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd");        try {            Date date = dateFormat.parse(getRelease);            SimpleDateFormat newFormat = new SimpleDateFormat("EEEE, dd MM yyyy");            String show = newFormat.format(date);            ReleaseDate.setText(show);        } catch (ParseException e){            e.printStackTrace();        }        Picasso.with(context).load("http://image.tmdb.org/t/p/w500/"+getPosterPath).into(Poster);    }}