package com.example.root.dicodingmovie;import org.json.JSONObject;public class MyModel {    private String title;    private String release;    private String overview;    private String icon_path;    private String backdrop_path;    public MyModel(JSONObject jsonObject){        try {            String my_title         = jsonObject.getString("title");            String my_release       = jsonObject.getString("release_date");            String my_overview      = jsonObject.getString("overview");            String my_icon_path     = jsonObject.getString("poster_path");            String my_backdrop_path = jsonObject.getString("backdrop_path");            this.title          = my_title;            this.release        = my_release;            this.overview       = my_overview;            this.icon_path      = my_icon_path;            this.backdrop_path  = my_backdrop_path;        } catch (Exception e){            e.printStackTrace();        }    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getRelease() {        return release;    }    public void setRelease(String release) {        this.release = release;    }    public String getOverview() {        return overview;    }    public void setOverview(String overview) {        this.overview = overview;    }    public String getIcon_path() {        return icon_path;    }    public void setIcon_path(String icon_path) {        this.icon_path = icon_path;    }    public String getBackdrop_path() {        return backdrop_path;    }    public void setBackdrop_path(String backdrop_path) {        this.backdrop_path = backdrop_path;    }}